INSERT INTO `news` (`id`, `source_id`, `title`, `subtitle`, `slug`, `text`, `subtext`, `source`, `link`, `image`, `video`, `date`, `time`, `view`, `datetime`) VALUES
(1, 459658, 'قیمت طلا صعودی شد                      ', 'باتضعیف ارزش دلار         ', 'قیمت-طلا-صعودی-شد', 'به گزارش اقتصاد آنلاین به نقل از فارس، امروز در بازارهای جهانی قیمت طلا با 26.4 دلار افزایش نسبت به روز گذشته با قیمت 1940.3 دلار معامله شد. قیمت فلز زرد امروز در کمترین زمان 1918.3 و در بیشترین زمان 1942.8 دلار به فروش رفت. قیمت این فلز گرانقیمت در مقایسه با یک ماه گذشته 130 و یکسال گذشته 439 دلار افزایش داشته است. در گروه سایر فلزات گرانبها هم نقره با رشد 1.21 دلار هر انس از این فلز به قیمت 26.66 دلار به فروش رفت. فلزات گرانبهای پلاتینوم و پالادیوم هم افزایش‌های به ترتیب 29 و 54 دلار را تجربه کردند. خبرگزاری رویترز نوشت: دلیل افزایش قیمت طلا امروز تحت تأثیر تضعیف ارزش دلار بود. زمانی که ارزش دلار پایین می‌آید، طلا را برای خریداران با دیگر ارزها ارزانتر می‌کند، بنابراین با افزایش تقاضا قیمت بالا می‌رود. اما دلیل دوم، کاهش بی‌سابقه قیمت طلا در روزهای گذشته بود و بازار امروز تا حدودی کاهش‌های یکی دو روز گذشته را جبران کرد. امروز ارزش دلار در مقابل سبد دیگری از ارزها 2 دهم درصد کاهش یافت و همین باعث ارزانی این فلز زرد برای خریداران با دیگر ارزها شد. قیمت هر اونس طلا که در روزهای گذشته به 2 هزار و 50 دلار هم رسیده بود، تنها با 80 دلار کاهش در یک روز به زیر 1900 برگشت.', 'امروز در بازارهای جهانی قیمت طلا تحت‌تأثیر تضعیف ارزش‌ دلار و نیز جبران کاهش‌های دو روز گذشته بیش از ۲۶دلار تقویت شد.', 1, 'https://www.eghtesadonline.com/%D8%A8%D8%AE%D8%B4-%D8%B7%D9%84%D8%A7-%D8%A7%D8%B1%D8%B2-7/459658-%D9%82%DB%8C%D9%85%D8%AA-%D8%B7%D9%84%D8%A7-%D8%B5%D8%B9%D9%88%D8%AF%DB%8C-%D8%B4%D8%AF/', 'https://static1.eghtesadonline.com/thumbnail/dp1ifDFfIyxW/qpW0ZmpbyUytX4aLRhq9RaAF56YLGeYks4ozQKcbCVmogOVBmfOx7Qk3HgW-lbmnwSiEBbYEBcS7shnK3SoCMxBNrr5PsKqUUNVYfLfxz4g,/%D9%82%DB%8C%D9%85%D8%AA+%D8%B7%D9%84%D8%A7.jpg', NULL, '۱۳۹۹/۰۵/۲۳', '۲۰:۵۵', 2, '2020-08-14 22:27:33'),
(2, 459624, 'قیمت دلار امروز چند؟ (۱۳۹۹/۵/۲۳)                       ', NULL, 'قیمت-دلار-امروز-چند', 'به گزارش اقتصادآنلاین به نقل از مهر، قیمت هر دلار آمریکا (اسکناس) امروز پنجشنبه ۲۳ مرداد ۹۹ در صرافی‌های بانکی به ۲۲ هزار و ۵۰ تومان رسید. همچنین امروز صرافی‌های بانکی هر دلار را ۲۱ هزار و ۹۵۰ تومان خریداری می‌کنند. صرافی‌ها امروز هر یورو را نیز ۲۵ هزار و ۹۰۰ تومان خریداری می‌کنند و نرخ فروش آن نیز ۲۶ هزار تومان است.', 'قیمت هر دلار آمریکا (اسکناس) امروز پنجشنبه ۲۳ مرداد ۹۹ در صرافی های بانکی به ۲۲ هزار و ۵۰ تومان رسید.', 1, 'https://www.eghtesadonline.com/%D8%A8%D8%AE%D8%B4-%D8%B7%D9%84%D8%A7-%D8%A7%D8%B1%D8%B2-7/459624-%D9%82%DB%8C%D9%85%D8%AA-%D8%AF%D9%84%D8%A7%D8%B1-%D8%A7%D9%85%D8%B1%D9%88%D8%B2-%DA%86%D9%86%D8%AF/', 'https://static3.eghtesadonline.com/thumbnail/mf4Voqlqz6gB/qpW0ZmpbyUytX4aLRhq9RaAF56YLGeYks4ozQKcbCVmogOVBmfOx7Qk3HgW-lbmnwSiEBbYEBcS7shnK3SoCM964hcPSJKl_UNVYfLfxz4g,/%DA%AF%D8%A7%D9%84%D8%B1%DB%8C+%D8%AF%D9%84%D8%A7%D8%B1.jpg', NULL, '۱۳۹۹/۰۵/۲۳', '۱۶:۴۷', 0, '2020-08-14 22:27:33'),
(3, 459619, 'خریداران سکه در سال‌۹۸ چقدر مالیات می‌دهند؟                      ', 'مشمولین مالیات مقطوع و تکالیف آن‌ها        ', 'خریداران-سکه-در-سال-چقدر-مالیات-می-دهند', 'به گزارش اقتصادآنلاین به نقل از فارس، امید علی پارسا رئیس کل سازمان امور مالیاتی کشور نحوه اخذ مالیات از خریداران سکه در سال ۹۸ را تعیین کرد. در متن این بخشنامه آمده است: با توجه به مفاد مواد (۱) و (۹۳) قانون مالیاتهای مستقیم اصلاحی مصوب ۱۳۹۴\\/۴\\/۳۱ و بنا به اختیار حاصل از تبصره ماده (۱۰۰) قانون مذکور و به منظور تکریم مودیان و تسهیل وصول مالیات عملکرد سال ۱۳۹۸ دریافت کنندگان سکه تمام بهار آزادی از بانک مرکزی جمهوری اسلامی ایران، مقرر می‌دارد: ۱- مشمولین مالیات مقطوع و تکالیف آنها ۱-۱- تمامی اشخاص حقیقی خریدار سکه از بانک مرکزی جمهوری اسلامی ایران که در سال ۱۳۹۸ به دریافت حداکثر (۱۸۵) قطعه سکه اقدام نموده مشمول مالیات مقطوع موضوع این دستور العمل می باشند. این گروه از خریداران سکه از نگهداری اسناد و مدارک موضوع قانون صدرالاشاره و تسلیم اظهارنامه مالیاتی معاف می باشد. بدیهی است با استناد قسمت اخیر تبصره ماده (۱۰۰) قانون مالیاتهای مستقیم اصلاحیه ۱۳۹۴\\/۰۴\\/۳۱ مالیات اشخاص موضوع این بند که اظهار نامه مالیاتی خود را در موعد مقرر تسلیم نمایند بر اساس رسیدگی به اسناد و مدارک ارائه شده تعیین خواهد شد. ۱-۲- مهلت پرداخت مالیات موضوع این دستورالعمل حداکثر تا پایان مردادماه سال ۱۳۹۹ می باشد خریداران مذکور مکلفند با مراجعه به سامانه عملیات الکترونیکی مودیان مالیاتی سازمان امور مالیاتی کشور به نشانی http:\\/\\/tax.gov.ir نسبت به پرداخت مطالبات متعلق به شرح بند (۲) این دستورالعمل اقدام نمایند. پرداخت مالیات بعد از سررسید تعیین شده موجب تعلق جریمه دیرکرد موضوع ماده ۱۹۰ قانون مالیات های مستقیم خواهد شد. میزان مالیات مقطوع قابل پرداخت اشخاص حقیقی دریافت کننده سکه براساس تعداد سکه تحویلی طی سال ۱۳۹۸ ۲-۱- تا ۱۰ سکه مشمول مالیات نمی باشد ۲-۲- نسبت به مازاد ۱۰ سکه تا ۳۰ که به ازای هر قطعه سکه ۲.۰۰۰.۰۰۰ ریال مالیات مقطوع ۲-۳- نسبت به مازاد ۳۰ سکه تا ۵۰ عدد سکه به ازای هر قطعه سکه ۵.۲۰۰.۰۰۰ ریال مالیات مقطوع ۲-۴- نسبت به مازاد ۵۰ قطعه سکه تا ۱۸۵ عدد سکه به ازای هر قطعه سکه ۶.۵۰۰.۰۰۰ ریال مالیات مقطوع ۳- سایر موارد ۳-۱- در خصوص اشخاصی که امکان پرداخت مالیات مقطوع موضوع این دستورالعمل را به صورت کامل تا پایان مردادماه سال ۱۳۹۹ ندارند، مالیات مقطوع تعیین شده حداکثر چهار ماه به صورت مساوی تقسیط می‌شود. ۳-۲- اشخاصی که در سال ۱۳۹۸ نسبت به دریافت بیش از (۱۸۵) قطعه سکه از بانک مرکزی اقدام نموده و همچنین صاحبان مشاغلی که حسب سوابق پرونده با مجوز صادره از سوی مراجع ذیربط به شغل مرتبط با خرید و فروش سکه اشتغال دارند مشمول این دستورالعمل نبوده و مکلف به تسلیم اظهارنامه مالیاتی و پرداخت مالیات متعلق در موعد مقرر پایان مردادماه ۱۳۹۹ می‌باشند. ۳-۳- از آنجا که مطابق حکم تبصره ماده (۱۰۰) قانون مالیاتهای مستقیم اصلاحی مصوب ۱۳۹۴\\/۴\\/۳۱ مالیات مقطوع تعیین شده مطابق بند (۲) این دستورالعمل در صورت عدم تسلیم اظهارنامه مالیاتی در موعد مقرر، قطعی و وفق مقررات قابل وصول می باشد لذا اوراق قطعی تمامی مشمولین موضوع بند (۱) این دستورالعمل، یک ماه پس از سررسید پرداخت مالیات صادر و به مودیان ذیربط ابلاغ خواهد شد. ۳-۴- آن دسته از مشمولین این دستورالعمل که به مالیات قطعی شده اعتراض داشته باشند اعتراض آنها برابر مقررات در هیأت حل اختلاف مالیاتی موضوع تبصره یک ماده (۲۱۶) قانون مالیاتهای مستقیم اصلاحی مصوب ۱۳۹۴\\/۰۴\\/۳۱ قابل رسیدگی خواهد بود.', 'رئیس کل سازمان امور مالیاتی مالیات خریداران سکه در سال ۹۸ را تعیین کرد.', 1, 'https://www.eghtesadonline.com/%D8%A8%D8%AE%D8%B4-%D8%B7%D9%84%D8%A7-%D8%A7%D8%B1%D8%B2-7/459619-%D8%AE%D8%B1%DB%8C%D8%AF%D8%A7%D8%B1%D8%A7%D9%86-%D8%B3%DA%A9%D9%87-%D8%AF%D8%B1-%D8%B3%D8%A7%D9%84-%DA%86%D9%82%D8%AF%D8%B1-%D9%85%D8%A7%D9%84%DB%8C%D8%A7%D8%AA-%D9%85%DB%8C-%D8%AF%D9%87%D9%86%D8%AF/', 'https://static1.eghtesadonline.com/thumbnail/CrjWnRr9yQ9e/qpW0ZmpbyUytX4aLRhq9RaAF56YLGeYks4ozQKcbCVmogOVBmfOx7Qk3HgW-lbmnwSiEBbYEBcS7shnK3SoCM9LISUwplKQnUNVYfLfxz4g,/%D8%B3%DA%A9%D9%87+%D8%B7%D9%84%D8%A7.jpg', NULL, '۱۳۹۹/۰۵/۲۳', '۱۶:۱۰', 0, '2020-08-14 22:27:33'),
(4, 459600, 'عقربه‌های طلا در چرخش نوسانی/ حرکت بعدی فلز زرد صعودی است یا نزولی؟                      ', 'اقتصادآنلاین گزارش می‌دهد؛        ', 'عقربه-های-طلا-در-چرخش-نوسانی-حرکت-بعدی-فلز-زرد-صعودی-است-یا-نزولی', 'اقتصادآنلاین - اکرم شعبانی؛ در حقیقت در معاملات روز گذشته معاملات آتی طلا قابل تحویل در ماه اکتبر با افزایش ۷.۷۰ دلاری در هر اونس به قیمت ۱۹۴۶ دلار معامله شد. قیمت نقره سپتامبر هم در کومکش با افزایش ۰.۰۳۱ دلاری به ۲۶.۰۸ دلار در هر اونس رسید. این در حالی است که بازارهای جهانی سهام هم روند صعودی خود را حفظ کرده‌اند روندی که متاثر از بسیاری علائم روانشناسی در بازار درباره اخبار منتشر شده مرتبط با ویروس کروناست. بسیاری معتقدند اعلام خبر تولید واکسن کرونا از سوی روسیه هم بر این روند تاثیرگذار بوده است. اگرچه هنوز مشخص نیست که این ادعا تا چه اندازه صحت دارد اما در حال حاضر صحبت از واکسنی است که ممکن است در سه ماهه آخر سال ۲۰۲۰ در دسترس باشد و یا در بدترین حالت در اوایل ۲۰۲۱ در دسترس خواهد بود. در هر صورت اقتصادهای بزرگ جهان در انتظار وقوع پیشرفتی هستند که شرایط اقتصادی را حتی به اوضاع پیش از کرونا نزدیک کند. در ایالات متحده میزان ابتلا به کووید - ۱۹ در هفته جاری رو به کاهش گذاشته است و این اتفاق با نزدیک شدن کنگره در تصویب بسته دوم محرک مالی همزمان شده که تاثیری مثبت بر بازارهای مختلف بر جای گذاشته است. همه مواردی که به آنها اشاره شد در بازارهای سهام و در بازار برخی کالاها موجب صعود شده در حالی که در بخش سرمایه‌های امن ایالات متحده و بر روند قیمت گذاری طلا و نقره تاثیر منفی بر جای گذاشته است. این در حالی است که حتی بازدهی اوراق ده ساله خزانه در طول این هفته صعود قابل توجهی داشته است. (نمودار حرکت ۲۴ ساعته قیمت طلا) از دیدگاه فنی، قیمت طلای آتی ماه اکتبر پس از دستیابی به کمترین قیمت سه هفته اخیر یعنی ۱۸۶۵ دلار در هر اونس کمی روند صعودی در پیش گرفت. این بدان معناست که روند صعودی از مزیت فنی بلندمدت برخوردار است اما در شرایط فعلی در پیش گرفتن روند اصلاحی با توجه به قیمت‌های اخیر امری طبیعی است. این در حالی است که افت قیمت‌ها در کمتر از این سطح هم وجود دارد اما باید توجه داشته باشیم که این روند نزولی دیدگاه کوتاه مدت دارد. هدف اصلی روند صعودی قیمت طلا دستیابی به سطح ۲ هزار دلار در هر اونس در معاملات ماه اکتبر در مقابل روند نزولی ۱۸۶۵ دلاری است. سطح مقاومت اول در این مسیر ۱۹۷۵ دلار و سپس ۲ هزار دلار خواهد بود. سطح حمایتی نخست هم ۱۹۲۵ دلار و سطح حمایتی دوم ۱۹۰۰ دلار در هر اونس خواهد بود. قیمت معاملات آتی نقره ماه سپتامبر هم تا حدودی روند رو به بالا داشت. معاملات نقره هم درست مانند معاملات طلا از مزیت فنی بلندمدت برخوردار است. قیمت نقره در پایین‌ترین سطح روز گذشته خود در معاملات به ۲۳.۵۸ دلار در هر اونس رسید. روند نزولی قیمت نقره هم همچون طلا کوتاه مدت است اما هدف اصلی روند صعودی قیمت طلا عبور از سطح ۲۸ دلار در هر اونس برآورد شده در حالی که هدف نزول توقف در همان سطح ۲۳.۵۸ دلار است. در ادامه معاملات نقره سطح مقاومت اول ۲۶.۵۰ دلار و سطح مقاومت دوم ۲۷ دلار برآورد شده در حالی که سطح پشتیبانی اول در سطح ۲۵.۵۰ دلار و سطح پشتیبانی دوم طلا ۲۵ دلار در هر اونس مشاهده شده است. (نمودار حرکت ۲۴ ساعته قیمت نقره)', 'قیمت معاملات آتی طلا و نقره در ظهر چهارشنبه اندکی صعودی بود. معاملات روز گذشته پس از افت شدید در معاملات سه شنبه، ثبات بیشتری را در قیمت این فلزات گرانبها بر جای گذاشت.', 1, 'https://www.eghtesadonline.com/%D8%A8%D8%AE%D8%B4-%D8%B7%D9%84%D8%A7-%D8%A7%D8%B1%D8%B2-7/459600-%D8%B9%D9%82%D8%B1%D8%A8%D9%87-%D9%87%D8%A7%DB%8C-%D8%B7%D9%84%D8%A7-%D8%AF%D8%B1-%DA%86%D8%B1%D8%AE%D8%B4-%D9%86%D9%88%D8%B3%D8%A7%D9%86%DB%8C-%D8%AD%D8%B1%DA%A9%D8%AA-%D8%A8%D8%B9%D8%AF%DB%8C-%D9%81%D9%84%D8%B2-%D8%B2%D8%B1%D8%AF-%D8%B5%D8%B9%D9%88%D8%AF%DB%8C-%D8%A7%D8%B3%D8%AA-%DB%8C%D8%A7-%D9%86%D8%B2%D9%88%D9%84%DB%8C/', 'https://static3.eghtesadonline.com/thumbnail/UMzqSAdCiieJ/qpW0ZmpbyUytX4aLRhq9RaAF56YLGeYks4ozQKcbCVmogOVBmfOx7Qk3HgW-lbmnwSiEBbYEBcS7shnK3SoCM2hM8VRBBtvqUNVYfLfxz4g,/%DA%AF%D8%A7%D9%84%D8%B1%DB%8C+%D8%B7%D9%84%D8%A7.jpg', NULL, '۱۳۹۹/۰۵/۲۳', '۱۴:۳۰', 0, '2020-08-14 22:27:33'),
(5, 459596, 'قیمت رسمی ۲۵ارز بانکی رشد کرد                      ', 'براساس اعلام بانک مرکزی،        ', 'قیمت-رسمی-ارز-بانکی-رشد-کرد', 'به گزارش اقتصادآنلاین به نقل از ایرنا، بانک مرکزی جمهوری اسلامی ایران امروز (پنج‌شنبه، ۲۳ مرداد) نرخ رسمی ۴۷ ارز عمده بین‌المللی را اعلام کرد. از این میان ۱۳ ارز با کاهش قیمت و ۲۵ ارز نیز با افزایش قیمت مواجه بودند. ارزش سایر ارزها ثابت باقی ماند. بر این اساس هر دلار آمریکا بدون تغییر نسبت به روزهای گذشته، ۴۲ هزار ریال قیمت خورد. هر پوند انگلیس به قیمت ۵۴ هزار و ۸۶۱ ریال و هر یورو نیز به قیمت ۴۹ هزار و ۶۰۶ ریال اعلام شد. همچنین در تابلوی بانک مرکزی، هر فرانک سوئیس ۴۶ هزار و ۱۱۸ ریال، کرون سوئد ۴ هزار و ۸۴۶۶ ریال، کرون نروژ ۴ هزار و ۷۱۲ ریال، کرون دانمارک ۶ هزار و ۶۶۲ ریال، روپیه هند ۵۶۲ ریال، درهم امارات متحده عربی ۱۱ هزار و ۴۳۷ ریال، دینار کویت ۱۳۷ هزار و ۲۰۸ ریال، یکصد روپیه پاکستان ۲۴ هزار و ۹۶۶ ریال، یکصد ین ژاپن ۳۹ هزار و ۳۶۵ ریال، دلار هنگ‌ کنگ ۵ هزار و ۴۱۹ ریال، ریال عمان ۱۰۹ هزار و ۲۳۱ ریال قیمت خورد. علاوه بر این، دلار کانادا ۳۱ هزار و ۷۲۵ ریال، دلار نیوزیلند ۲۷ هزار و ۵۹۹ ریال، راند آفریقای جنوبی دو هزار و ۴۰۵ ریال، لیر ترکیه ۵ هزار و ۷۳۰ ریال، روبل روسیه ۵۷۲ ریال، ریال قطر ۱۱ هزار و ۵۳۹ ریال، یکصد دینار عراق ۳ هزار و ۵۲۴ ریال، لیر سوریه ۸۳ ریال، دلار استرالیا ۳۰ هزار و ۱۱۸ ریال، ریال سعودی ۱۱ هزار و ۲۰۰ ریال، دینار بحرین ۱۱۱ هزار و ۷۰۱ ریال، دلار سنگاپور ۳۰ هزار و ۶۳۳ ریال، یکصد تاکای بنگلادش ۴۹ هزار و ۴۵۱ ریال، ده روپیه سریلانکا ۲ هزار و ۳۰۱ ریال، کیات میانمار ۳۱ ریال و یکصد روپیه نپال ۳۴ هزار و ۹۲۳ ریال، یکصد درام ارمنستان ۸ هزار و ۷۲۱ ریال، دینار لیبی ۳۰ هزار و ۶۶۸ ریال، تعیین شد. امروز هر یوان چین ۶ هزار و ۵۲ ریال، یکصد بات تایلند ۱۳۵ هزار ۲۸۱ ریال، رینگیت مالزی ۱۰ هزار و ۳۰ ریال، یک هزار وون کره جنوبی ۳۵ هزار و ۴۹۱ ریال، دینار اردن ۵۹ هزار و ۲۳۸ یال، یکصد تنگه قزاقستان ۱۰ هزار و ۴۰ ریال، لاری گرجستان ۱۳ هزار و ۶۴۱ ریال، یک هزار روپیه اندونزی ۲ هزار و ۸۳۵ ریال، افغانی افغانستان ۵۴۶ ریال، روبل جدید بلاروس ۱۷ هزار ۹۸ ریال، منات آذربایجان ۲۴ هزار و ۷۲۱ ریال، یکصد پزوی فیلیپین ۸۵ هزار و ۸۸۹ ریال، سامانی تاجیکستان ۴ هزار و ۷۳ ریال، بولیوار جدید ونزوئلا ۴ هزار و ۲۰۶ ریال و منات جدید ترکمنستان ۱۱ هزار و ۹۶۶ ریال ارزش‌گذاری شد.', 'بانک مرکزی امروز (پنج‌شنبه) با اعلام رسمی نرخ ۴۷ ارز عمده، از کاهش افزایش بهای ۲۵ ارز و کاهش ارزش ۱۳ ارز خبر داد.', 1, 'https://www.eghtesadonline.com/%D8%A8%D8%AE%D8%B4-%D8%B7%D9%84%D8%A7-%D8%A7%D8%B1%D8%B2-7/459596-%D9%82%DB%8C%D9%85%D8%AA-%D8%B1%D8%B3%D9%85%DB%8C-%D8%A7%D8%B1%D8%B2-%D8%A8%D8%A7%D9%86%DA%A9%DB%8C-%D8%B1%D8%B4%D8%AF-%DA%A9%D8%B1%D8%AF/', 'https://static1.eghtesadonline.com/thumbnail/jQxMCUfahh1x/qpW0ZmpbyUytX4aLRhq9RaAF56YLGeYks4ozQKcbCVmogOVBmfOx7Qk3HgW-lbmnwSiEBbYEBcS7shnK3SoCMwoBF5JrbzM7UNVYfLfxz4g,/%DA%AF%D8%A7%D9%84%D8%B1%DB%8C+%D8%AF%D9%84%D8%A7%D8%B1.jpg', NULL, '۱۳۹۹/۰۵/۲۳', '۱۳:۵۴', 0, '2020-08-14 22:27:33'),
(6, 459594, 'قیمت سکه به ۱۰میلیون و ۷۰۰هزار تومان رسید(۱۳۹۹/۵/۲۳)                       ', NULL, 'قیمت-سکه-به-میلیون-هزار-تومان-رسید', 'به گزارش اقتصادآنلاین به نقل از مهر، قیمت هر قطعه سکه تمام بهار آزادی طرح جدید امروز پنج شنبه، ۲۳ مرداد ۹۹ در معاملات بازار آزاد تهران به ۱۰ میلیون و ۷۰۰ هزار تومان رسید. همچنین قیمت هر قطعه سکه تمام بهار آزادی طرح قدیم ۱۰ میلیون و ۲۰۰ هزار تومان، نیم سکه ۵ میلیون و ۳۵۰ هزار تومان، ربع سکه ۳ میلیون و سکه یک گرمی یک میلیون و ۷۰۰ هزار تومان است. همچنین قیمت هر اونس طلا در بازارهای جهانی ۱۹۲۹ دلار و۱۰ سنت و هر گرم طلای ۱۸ عیار یک میلیون و ۲۸ هزار و ۴۳۶ تومان است.', 'قیمت هر قطعه سکه تمام بهار آزادی طرح جدید امروز پنج شنبه، ۲۳ مرداد ۹۹ در معاملات بازار آزاد تهران به ۱۰ میلیون و ۷۰۰ هزار تومان رسید.', 1, 'https://www.eghtesadonline.com/%D8%A8%D8%AE%D8%B4-%D8%B7%D9%84%D8%A7-%D8%A7%D8%B1%D8%B2-7/459594-%D9%82%DB%8C%D9%85%D8%AA-%D8%B3%DA%A9%D9%87-%D8%A8%D9%87-%D9%85%DB%8C%D9%84%DB%8C%D9%88%D9%86-%D9%87%D8%B2%D8%A7%D8%B1-%D8%AA%D9%88%D9%85%D8%A7%D9%86-%D8%B1%D8%B3%DB%8C%D8%AF/', 'https://static1.eghtesadonline.com/thumbnail/WjfYQ1Aankrq/qpW0ZmpbyUytX4aLRhq9RaAF56YLGeYks4ozQKcbCVmogOVBmfOx7Qk3HgW-lbmnwSiEBbYEBcS7shnK3SoCM17-GhrvSTk4UNVYfLfxz4g,/%D8%B3%DA%A9%D9%87.jpg', NULL, '۱۳۹۹/۰۵/۲۳', '۱۳:۴۷', 0, '2020-08-14 22:27:33'),
(7, 459579, 'رکود بریتانیا به کمک طلا آمد                      ', NULL, 'رکود-بریتانیا-به-کمک-طلا-آمد', 'به گزارش اقتصادآنلاین به نقل از دنیای اقتصاد، در حالی‌که روز سه‌شنبه ارزش اونس طلا با افت بسیار شدید ۷۲\\/ ۵ درصدی روبه‌رو شده بود و به قیمت هزار و ۹۱۱ دلاری رسیده بود، این فلز ارزشمند روز چهارشنبه را نیز نزولی آغاز کرد تا آنجایی که ارزش هر اونس آن از مرز هزار و ۹۰۰ دلار نیز پایین‌تر رفت و در برخی از ساعات تا قیمت هزار و ۸۶۴ دلار نیز پایین آمد. افت ارزش طلا در روز سه‌شنبه بزرگ‌ترین افت قیمت اونس در هفت سال گذشته بوده است، اما با این وجود طلا در سال جاری همچنان با رشدی ۲۸ درصدی جزو بازارهای پیشرو به حساب می‌آید. اما در ادامه روز و با انتشار رشد سه‌ماهه دوم اقتصاد بریتانیا که حاکی از یک رکود بی‌سابقه است، مجددا تقاضای طلا افزایش پیدا کرد. آمارها نشان می‌دهد، ششمین اقتصاد بزرگ جهان، سه‌ماه دوم سال جاری ۴\\/ ۲۰ درصد کوچک‌تر شده است که این حجم از انقباض برای بریتانیا یک رکورد تاریخی است. پس از انتشار آمار اقتصادی بریتانیا طلا مجددا به بالای مرز هزار و ۹۳۰ دلاری بازگشت. تحلیل‌گران بازار طلا معتقدند با وجود افت قیمتی طلا در روزهای اخیر، عوامل بنیادی که سبب افزایش قیمت طلا می‌شوند همچنان بدون هیچ تغییری در بازارها حضور دارند و از طرفی با توجه به رفتار اونس در بحران‌های گذشته، دسترسی طلا به قیمت ۳ هزار دلاری نیز دور از دسترس نخواهد بود. بریتانیا در بحران اقتصادی به نظر می‌آید بریتانیا رکورد رکود در یک فصل را در میان تمامی اقتصادهای بزرگ دنیا شکسته باشد. ششمین اقتصاد بزرگ جهان در بازه زمانی آوریل تا ژوئن با حجم بی‌سابقه ۴\\/ ۲۰ درصد منقبض شده است. این رکود اقتصادی همزمان است با دوره‌ای که محدودیت‌های اجتماعی و قرنطینه در سطح شدیدی از سوی دولت این کشور پیگیری شده بود. این رکود بی‌سابقه از اکنون نگرانی‌های بسیاری را درباره افزایش نرخ بیکاری در این کشور تقویت کرده است. پس از انتشار داده‌های فصلی ایالات متحده و اقتصاد ناحیه یورو، این سومین خبر مهم درباره کاهش شدید و بی‌سابقه تولید ناخالص داخلی از اقتصادهای تاثیرگذار در جهان است. این دومین فصل متوالی است که اقتصاد بریتانیا رشد منفی را تجربه می‌کند؛ موضوعی که برخی از تحلیلگران آن را نشانه رسمی ورود یک اقتصاد به رکود می‌دانند. بهبود نسبی در ماه ژوئن در همین حال داده‌های رسمی منتشر شده نشان می‌دهد که در ماه ژوئن تولیدات اقتصادی نسبت به ماه قبل از خود یعنی مه ‌با افزایش ۷\\/ ۸ درصدی روبه‌‌رو شده است. با اینکه برخی این آمار را نشانه‌ای از بازگشت آرام اقتصاد این کشور به وضعیت قبل از بحران قلمداد می‌کنند، اما برخی از تحلیل‌گران افزایش ماهانه تولیدات اقتصادی بریتانیا را اثر ناگهانی گشایش مجدد کسب‌وکارهایی می‌دانند که در دوره قرنطینه به‌طور کامل تعطیل شده بودند. با اینکه تولید ناخالص داخلی بریتانیا در ماه ژوئن مجددا روند افزایشی به خود گرفته است، اما حجم اقتصاد این کشور همچنان قابل مقایسه با ماه فوریه یعنی قبل از شیوع همه‌گیری کرونا نیست. هفته گذشته بانک مرکزی انگلیس اعلام کرد که پیش‌بینی‌های این نهاد نشان می‌دهد اقتصاد این کشور احتمالا تا فصل پایانی سال ۲۰۲۱ به سطح قبل از بحران خود بازنخواهد گشت. مسیر آینده اونس کجاست؟ با اینکه پاسخ دادن به این سوال در حال حاضر و با توجه به بالا بودن سطح نااطمینانی‌ها بسیار سخت است ولی به‌طور کلی می‌توان ردپای دو مسیر احتمالی اونس در بازارها را جست‌وجو کرد. نظر اغلب کارشناسان بر این است که به‌‌رغم افت قیمت طلا در روزهای گذشته، عوامل بنیادی تقویت‌کننده تقاضای اونس در بازارها همچنان دست نخورده باقی‌مانده‌اند. از همین رو تحلیل‌گران افت ارزش طلا در روزهای گذشته را تنها اصلاح قیمتی این فلز ارزشمند پس از رالی پر سرعت چند هفته گذشته ارزیابی می‌کنند. عوامل بنیادی تقویت‌کننده ارزش طلا عبارتند از چشم‌انداز اقتصادی ضعیف جهانی، افزایش تورم و کاهش ارزش دلار و در نهایت نرخ‌های بهره پایینی که به نظر می‌رسد بانک‌های مرکزی فعلا تمایلی به افزایش آن ندارند. علاوه بر این عوامل می‌توان عواملی مانند افزایش سطح تنش‌های ژئوپلیتیک و بحران بدهی دولت‌ها را نیز افزود. از سوی دیگر محاسبات نشان می‌دهد قیمت حقیقی کنونی اونس هنوز به سقف قیمتی سال‌های ۱۹۸۰ و ۲۰۱۱ نرسیده است. جو فاستر تحلیلگر بازاها در VanEck International Investors Gold Fund معتقد است اگر رفتار تاریخی طلا در زمان بحران مالی سال ۲۰۰۸ در این دوره نیز تکرار شود دسترسی طلا به مرز ۳ هزار دلاری به هیچ وجه دور از دسترس نیست. مسیر دیگر احتمالی اونس به شرایط کنترل بحران کرونا بستگی دارد. در صورتی که کشف واکسنی احتمالی و تولید عمده آن بتواند سبب کنترل کامل جهان بر این ویروس شود، بسیاری از عواملی که در راستای افزایش قیمت طلا عمل می‌کنند ممکن است از بین بروند، در این صورت احتمال کاهش ارزش اونس کم نخواهد بود.', 'طلا در حالی که در معاملات ابتدایی روز چهارشنبه در حال ادامه سقوط ارزش روز گذشته خود بود، در میانه روز تغییر جهت داد و مجددا روندی صعودی به خود گرفت.', 1, 'https://www.eghtesadonline.com/%D8%A8%D8%AE%D8%B4-%D8%B7%D9%84%D8%A7-%D8%A7%D8%B1%D8%B2-7/459579-%D8%B1%DA%A9%D9%88%D8%AF-%D8%A8%D8%B1%DB%8C%D8%AA%D8%A7%D9%86%DB%8C%D8%A7-%D8%A8%D9%87-%DA%A9%D9%85%DA%A9-%D8%B7%D9%84%D8%A7-%D8%A2%D9%85%D8%AF/', 'https://static1.eghtesadonline.com/thumbnail/CZz8uiRgaSYA/qpW0ZmpbyUytX4aLRhq9RaAF56YLGeYks4ozQKcbCVmogOVBmfOx7Qk3HgW-lbmnwSiEBbYEBcS7shnK3SoCM0clTmzH2j1nUNVYfLfxz4g,/cEsGcQss1gre.jpg', NULL, '۱۳۹۹/۰۵/۲۳', '۱۲:۱۶', 0, '2020-08-14 22:27:33'),
(8, 459578, 'ارزیابی قیمت طلا نسبت به شاخص داو‌جونز/ نمودار ۱۰۵ساله طلا در برابر داوجونز                      ', 'اقتصادآنلاین گزارش می‌دهد؛        ', 'ارزیابی-قیمت-طلا-نسبت-به-شاخص-داو-جونز-نمودار-ساله-طلا-در-برابر-داوجونز', 'اقتصادآنلاین - اکرم شعبانی؛ معاملات در بازارهای مالی و به طور خاص در بازار اس‌اندپی۵۰۰ در سطحی بسیار بالاتر و قابل توجه‌تر از ماه فوریه بسته شد. در عین حال شاخص داو جونز هم رکوردی بسیار بهتر از اوج خود در ماه فوریه به ثبت رساند. در نمودار ۱۰۵ ساله بلو چیپ در چنین شرایطی این سوال مطرح می‌شود که چند اونس طلا با این قیمت برای خرید شاخص داو جونز لازم است؟ در اواخر دهه ۹۰ و اوایل ۲۰۰۰ هنگامی که طلا بر روی سطح ۲۰۰ و ۳۰۰ دلار قرار گرفته بود، حدود ۴۰ اونس برای خرید شاخص داو جونز نیاز بود. در کمترین چرخه پیشین در سال ۱۹۳۳ حدود ۱.۹ اونس طلا و در ژانویه سال ۱۹۸۰ حدود ۱.۳ اونس طلا برای این منظور نیاز بود و این زمانی بود که با در نظر گرفتن تعدیل تورمی طلا در بالاترین سطح تاریخی خود قرار گرفته بود یعنی معادل حدودی ۲۵۰۰ دلار در هر اونس امروزی. هنگامی که قیمت اسمی طلا برای نخستین بار در آگوست سال ۲۰۱۱ به ۱۹۰۰ دلار رسید این نسبت در حدود ۶.۴ اونس بود. روز چهارشنبه این هفته اما نسبت شاخص داو جونز به طلا بر اساس قیمت میانگین پایانی ۱۴.۵ اونس بود و آخرین بار در اکتبر سال ۲۰۱۶ چنین نسبتی ایجاد شده بود، یعنی زمانی که قیمت طلا به ۱۲۶۵ دلار در هر اونس رسیده بود. در باشکوه‌ترین قیمت گذاری اخیر -هر چند این قیمت بسیار کوتاه مدت بود- زمانی که در معاملات جمعه گذشته طلا به ۲۰۸۹.۲۰ دلار در هر اونس رسیده بود، نسبت طلا به شاخص داو جونز به ۱۳.۴ اونس رسیده بود. در این زمینه بخوانید: نگاهی به سقوط بیش از ۱۰۰دلاری طلا در یک روز پشت پرده عرضه اسلب در بورس کالا نقش طلا در تورم‌های ناشی از بسته‌های محرک مالی (نمودار ۱۰۵ ساله نسبت طلا به شاخص داوجونز)', 'روز چهارشنبه طلا پس از سقوط بزرگ بیش از ۱۰۰ دلاری در هر اونس موفق به بازیابی بخشی از سود خود شد. قیمت طلای آتی قابل تحویل در ماه دسامبر بر روی ۱۹۲۶ دلار در هر اونس در بازار نیویورک بسته شد، در حالی که در ابتدای معاملات روز قیمت به ۱۸۷۴ دلار در هر اونس هم رسیده بود.', 1, 'https://www.eghtesadonline.com/%D8%A8%D8%AE%D8%B4-%D8%B7%D9%84%D8%A7-%D8%A7%D8%B1%D8%B2-7/459578-%D8%A7%D8%B1%D8%B2%DB%8C%D8%A7%D8%A8%DB%8C-%D9%82%DB%8C%D9%85%D8%AA-%D8%B7%D9%84%D8%A7-%D9%86%D8%B3%D8%A8%D8%AA-%D8%A8%D9%87-%D8%B4%D8%A7%D8%AE%D8%B5-%D8%AF%D8%A7%D9%88-%D8%AC%D9%88%D9%86%D8%B2-%D9%86%D9%85%D9%88%D8%AF%D8%A7%D8%B1-%D8%B3%D8%A7%D9%84%D9%87-%D8%B7%D9%84%D8%A7-%D8%AF%D8%B1-%D8%A8%D8%B1%D8%A7%D8%A8%D8%B1-%D8%AF%D8%A7%D9%88%D8%AC%D9%88%D9%86%D8%B2/', 'https://static3.eghtesadonline.com/thumbnail/PhKlKB8Kf9Te/qpW0ZmpbyUytX4aLRhq9RaAF56YLGeYks4ozQKcbCVmogOVBmfOx7Qk3HgW-lbmnwSiEBbYEBcS7shnK3SoCM4gfn4YRxuNvUNVYfLfxz4g,/%DA%AF%D8%A7%D9%84%D8%B1%DB%8C+%D8%B7%D9%84%D8%A7.jpg', NULL, '۱۳۹۹/۰۵/۲۳', '۱۲:۰۶', 0, '2020-08-14 22:27:33'),
(9, 459577, 'سکه از اونس سیگنال گرفت                      ', 'استراحت دلار در کانال جدید؛        ', 'سکه-از-اونس-سیگنال-گرفت', 'به گزارش اقتصادآنلاین به نقل از دنیای اقتصاد، طلای جهانی حتی در مقطعی به زیر کانال هزار و ۹۰۰ دلاری رفت ولی توانست در ادامه روز و تا ساعت ۴ بعدازظهر خود را به محدوده هزار و ۹۳۰ دلاری برساند. به گفته فعالان، سکه تحت فشار اونس قرار دارد و در صورتی که طلا افت بیشتری را به ثبت رساند، سکه ظرفیت رفتن به زیر مرز روانی مهم ۱۰ میلیون تومانی را خواهد داشت. با این حال، تنها عاملی که می‌تواند در صورت نزول اونس به ماندگاری سکه در کانال ۱۰ میلیون تومانی کمک کند، افزایش بهای دلار است. برخی فعالان اعتقاد داشتند یکی از عواملی که کمک کرد، دیروز قیمت سکه افت بیشتری را تجربه نکند، ورود بهای دلار به کانال ۲۲ هزار تومانی بود. در واقع ابزار دلار مانع از آن شد که سکه افت بیشتری را تجربه کند. باید دید در روزهای آتی دلار هم سوی سکه کاهشی خواهد شد یا اینکه طلا از قیمت‌های فعلی فاصله خواهد گرفت و به این ترتیب، انتظار افزایش قیمت را در بازار سکه زنده خواهد کرد. به گفته فعالان، در صورتی که طلای جهانی به بالای هزار و ۹۵۰ دلار بازگردد، می‌توان انتظار داشت که خریداران در بازار سکه بیشتر شوند. کاهش نوسانات دلار در چهارمین روز کاری هفته جاری، شدت نوسانات در بازار ارز بسیار کاهش یافت و خبری از رفت و برگشت‌های کانالی نبود. روز چهارشنبه،‌ دلار در همان ابتدای روز وارد کانال ۲۲ هزار تومانی شد و درادامه نیز توان زیادی برای پیشروی در این کانال از خود نشان نداد. دلار که ساعت ۱۱ صبح معاملات خود را روی عدد ۲۲ هزار و ۵۰ تومان آغاز کرده بود، حدود ساعت ۴ بعدازظهر با قیمت ۲۲ هزار و ۲۷۰ تومان به فروش می‌رفت که نسبت به قیمت ابتدای روز افزایش ۲۲۰ تومانی را نشان می‌داد و در مقایسه با قیمت بسته شده روز سه شنبه، ۳۲۰ تومان بیشتر بود. دلار روز سه شنبه نیز افزایش ۳۵۰ تومانی را به ثبت رسانده بود، ولی در آن مقطع زمانی دامنه نوسانات بسیار شدیدتر از روز چهارشنبه بود. در بیست و یکمین روز خردادماه، اسکناس آمریکایی یک بار به سقف ۲۲ هزار و ۷۰۰ تومانی برخورد کرد و بعد با نزول قیمت به انتهای کانال ۲۱ هزار تومانی رفت و دوباره در معاملات پشت خطی وارد کانال ۲۲ هزار تومانی شد. در واقع، روز سه شنبه دامنه نوسان دلار در حدود ۵ درصد بود ولی بازه تغییرات نوسانی در روز پنجم هفته به کمتر از ۲ درصد رسید. برخی فعالان، کاهش نوسان دلار در روز چهارشنبه را ناشی از استراحت معامله‌گران بازار می‌دانستند؛ مقصود آنها از استراحت این بود که خریداران و فروشندگان پس از چند روز خرید و فروش و نوسانات شدید، سعی کردند بیشتر تماشاچی وقایع بازار باشند تا جهت قیمت مشخص تر شود. برخی از آنها در روزهای اخیر دچار ضرر سنگینی شده بودند و افرادی هم که سود کرده بودند، اطمینان نداشتند که دلار به راحتی بتواند به کانال ۲۳ هزار تومان بازگردد. در کنار این، اخبار سیاسی زیادی این روزها به گوش بازار می‌رسد و به نظر می‌رسد معامله‌گران منتظر هستند که تکلیف برخی از این اخبار مشخص شود و پس از آن موقعیت خرید و فروش خود را تعیین کنند. عامل اصلی نوسانات روزهای اخیر هم خبرهای متناقض بود و انتظارات متفاوتی که هر روز پیرامون خبرها ایجاد می‌شد؛ با این حال، حداقل روز چهارشنبه دیگر بازار نسبت به اخبار واکنش خاصی نداشت. روز چهارشنبه از سمت حواله درهم نیز سیگنال کاهشی یا افزایشی خاصی، به بازار داخلی ارسال نشد. در این روز حواله درهم عمدتا در محدوده ۶ هزار و ۱۲۰ تا ۶ هزار و ۱۷۰ تومان معامله شد و دامنه نوسان آن هم محدودتر از روزهای قبل بود. دیروز ارزش حواله درهم و دلار بیشتر از هر زمانی به یکدیگر نزدیک شدند و همین مساله، خبر از برقراری یک تعادل در بازار می‌داد.', 'در بازار سکه دیروز اوضاع بیشتر با کاهشی‌های بازار یار بود. روز چهارشنبه فلز گرانبهای داخلی ۵۵۰ هزار تومان از ارزش خود را از دست داد و با پایین رفتن از سطح ۱۰ میلیون و ۵۰۰ هزار تومانی به بهای ۱۰ میلیون و ۴۰۰ هزار تومان رسید. افت سکه بیشتر به دلیل نوساناتی بود که در بازارهای جهانی برای طلا صورت گرفت و قیمت این فلز گرانبها در بازار داخلی را تحت فشار قرار داد.', 1, 'https://www.eghtesadonline.com/%D8%A8%D8%AE%D8%B4-%D8%B7%D9%84%D8%A7-%D8%A7%D8%B1%D8%B2-7/459577-%D8%B3%DA%A9%D9%87-%D8%A7%D8%B2-%D8%A7%D9%88%D9%86%D8%B3-%D8%B3%DB%8C%DA%AF%D9%86%D8%A7%D9%84-%DA%AF%D8%B1%D9%81%D8%AA/', 'https://static3.eghtesadonline.com/thumbnail/rQjpBvPEvKFx/qpW0ZmpbyUytX4aLRhq9RaAF56YLGeYks4ozQKcbCVmogOVBmfOx7Qk3HgW-lbmnwSiEBbYEBcS7shnK3SoCM8TLxvYp5jkLUNVYfLfxz4g,/8VOjMZIJ4eNQ.jpg', NULL, '۱۳۹۹/۰۵/۲۳', '۱۱:۵۲', 0, '2020-08-14 22:27:33'),
(10, 459554, 'آیا طلا به دوران اوج برمی‌گردد؟                      ', NULL, 'آیا-طلا-به-دوران-اوج-برمی-گردد', 'به گزارش اقتصادآنلاین به نقل از ایسنا، تحلیلگران کومرس بانک معتقدند اگرچه این عقب نشینی عظیم به معنای آن نیست که روند صعودی این فلز زرد به پایان رسیده اما بازگشت طلا به اوج جدید اخیرش به سرعت راه رفتن در پارک روی نمی دهد. کارستن فریتچ، تحلیلگر کومرس بانک در این باره اظهار کرد: بهای هر دو فلز ارزشمند طلا و نقره سقوط کرد. طلا حدود شش درصد نزول کرد و بزرگترین ریزش قیمت روزانه از آوریل سال ۲۰۱۳ تاکنون را تجربه کرد و نقره ضربه شدیدتری دید و ۱۵ درصد سقوط قیمت داشت که بزرگترین کاهش یک روزه بهای این فلز از اکتبر سال ۲۰۰۸ تاکنون بود. در مقایسه با اوج قیمت جمعه، طلا حدود ۲۰۰ دلار و نقره شش دلار ریزش قیمت داشته است. طلا پس از ریزش قیمتی که در سال ۲۰۱۳ پیدا کرد، هفت سال طول کشید تا به بالاترین رکورد همه زمانها صعود کند. این بار بهبود قیمت طلا منتفی نیست اما روند بهبود آن سریع اتفاق نخواهد افتاد. به گفته این تحلیلگر، بعید است قیمتها به سرعت به اوج قیمتی که در پایان هفته گذشته داشتند، برگردند. با این حال تنها عامل متفاوت مهمی که این بار وجود دارد فضای کلان است که به نفع قیمتهای بالاتر طلا و نقره مانده است. نرخهای بهره واقعی منفی، افزایش غیرمنتظره تزریق نقدینگی به بازار و جهش عظیم بدهی، فضا برای صعود قیمت فلزات ارزشمند را مساعد کرده است. جروم پاول، رییس بانک مرکزی آمریکا در آخرین کنفرانس مطبوعاتی خود روشن کرد مسئله افزایش نرخهای بهره که هفت سال پیش مطرح شدن آن باعث سقوط سهمگین قیمت طلا شد، به این زودی اتفاق نخواهد افتاد. بر اساس گزارش کیتکونیوز، با این حال، روند احیای سریع قیمت طلا به این زودی روی نخواهد داد زیرا سقوط قیمت در روز سه شنبه صدمات فنی زیادی به بار آورد و این وضعیت در خروج سرمایه از صندوقهای ETF طلا منعکس شد. خروج سرمایه از این صندوقها در سه روز متوالی گذشته ثبت شده است و صندوقهای ETF نقره هم روز سه شنبه با بزرگترین خروج روزانه سرمایه روبرو شدند. بهای هر اونس طلا در بازار معاملات آتی آمریکا روز سه شنبه ۹۳ دلار و ۴۰ سنت معادل ۴.۵۸ درصد کاهش یافت و در ۱۹۴۶ دلار و ۳۰ سنت بسته شد و بزرگترین رکورد کاهش قیمت یک روزه از ۱۵ آوریل سال ۲۰۱۳ تاکنون را شکست. هر اونس طلا که در اوایل معاملات روز چهارشنبه در سطح ۱۸۷۴ معامله می شد آخرین بار در معاملات بازار آمریکا به ۱۹۵۲ دلار رسید.', 'صعود تابستانی طلا روز سه شنبه توقف ناگهانی پیدا کرد و این فلز ارزشمند با بزرگترین کاهش یک روزه از سال ۲۰۱۳ تاکنون روبه رو شد.', 1, 'https://www.eghtesadonline.com/%D8%A8%D8%AE%D8%B4-%D8%B7%D9%84%D8%A7-%D8%A7%D8%B1%D8%B2-7/459554-%D8%A2%DB%8C%D8%A7-%D8%B7%D9%84%D8%A7-%D8%A8%D9%87-%D8%AF%D9%88%D8%B1%D8%A7%D9%86-%D8%A7%D9%88%D8%AC-%D8%A8%D8%B1%D9%85%DB%8C-%DA%AF%D8%B1%D8%AF%D8%AF/', 'https://static2.eghtesadonline.com/thumbnail/5mY9MnZZFeQt/qpW0ZmpbyUytX4aLRhq9RaAF56YLGeYks4ozQKcbCVmogOVBmfOx7Qk3HgW-lbmnwSiEBbYEBcS7shnK3SoCM5S8EADRdEHMUNVYfLfxz4g,/%D8%B7%D9%84%D8%A7.jpg', NULL, '۱۳۹۹/۰۵/۲۳', '۰۹:۵۹', 0, '2020-08-14 22:27:33'),
(11, 459508, 'دلار ریزش کرد                      ', 'برای دومین روز متوالی        ', 'دلار-ریزش-کرد', 'به گزارش رویترز، همان طور که پیشتر نیز انتظار می رفت، با بازگشایی مجدد کسب و کارها و افزایش تزریق نقدینگی توسط بانک مرکزی به بازارهای مالی، تورم آمریکا صعودی محسوس را تجربه کرد تا جایی که متوسط نرخ تورم بزرگ‌ترین اقتصاد جهان در ۱۲ ماه منتهی به ژوییه با ۰.۴ درصد افزایش نسبت به دوره مشابه منتهی به ماه قبل به سطح یک درصد رسید. به گزارش اقتصادآنلاین به نقل از ایسنا، این نرخ تورم که بالاترین سطح تورمی ثبت شده در آمریکا در هفت ماهه اخیر بوده، همچنین بزرگ ترین افزایش ماهانه تورم آمریکا از ژانویه ۱۹۹۱ بدین سو نیز محسوب می شود. برخی از کارشناسان هشدار داده اند در صورت کاهش بیشتر نرخ بهره و تداوم روند تزریق نقدینگی، باز هم شاهد افزایش تورم باشیم البته با توجه به هدف گذاری سطح دو درصدی نرخ تورم، به نظر می رسد فدرال رزرو بیشتر نگران افزایش بدهی ها است تا نرخ توم. پیش از این وزارت کار آمریکا اعلام کرده بود نرخ بیکاری بزرگ ترین اقتصاد جهان تا پایان ماه ژوئیه باز هم کاهشی شده و رقم ۱۰.۲ درصد را ثبت کرده است. این نرخ بیکاری که ۰.۹ درصد کمتر از ماه قبل است باعث افزایش امیدها به تک رقمی شدن نرخ بیکاری در ماه جاری شده است. ترامپ در واکنش به این روند گفته است: نرخ بیکاری خیلی سریع تر از چیزی که خیلی ها فکر می کردند در حال کاهش است. سرنوشت بسته حمایتی دولت از کسب وکارها و شهروندان نیز یکی دیگر از عوامل موثر بر معاملات بازار ارز است. استیون منوچین- وزیر خزانه داری آمریکا پس از رایزنی با سران دمواکرات از احتمال نرسیدن به توافق با آن ها خبر داده و نانسی پلوسی- رهبر کنگره نیز گفته است دو طرف مایل ها از رسیدن به اشتراک نظر با یکدیگر فاصله دارند. میزان کمک هزینه پرداختی به بیکاران، میزان کمک دولتی فدرال به دولت های محلی و تخصیص بودجه بیشتر به مراکز آموزشی و درمانی از مهم ترین موارد اختلافی بین دو طرف به شمار می رود. رهبر مجلس سنا که موافق اجرایی شدن این بسته است، دموکرات ها را مقصر این کارشکنی معرفی کرده است. پس از آن که رایزنی مقامات دولت ترامپ با رهبران کنگره در خصوص تسریع در اجرای بسته حمایتی جدید به نتیجه ای نرسید، رئیس جمهور آمریکا در اقدامی غیرمنتظره با امضای فرمانی اجرایی دستور داد بیمه بیکاری برای افرادی که شغل خود را از دست داده اند برقرار و به آن ها مبلغ ۴۰۰ دلار پرداخت شود. ترامپ با دفاع از این تصمیم خود وعده داده است در صورت انتخاب مجدد به عنوان رئیس جمهور، مالیات بر درآمدهای سرمایه ای را برای خانوارهای طبقه متوسط کاهش دهد. وی افزود: دلیلی وجود ندارد که اقتصادمان در سه ماهه سوم امسال ۲۰ درصد رشد نکند. به گفته ترامپ، کاهش مالیات ها می تواند باعث افزایش اشتغال زایی شود. لایحه کاهش مالیاتی که تقریبا دوسال پس از انتخاب ترامپ اجرایی شد از سوی وی و جمهوری خواهان به عنوان یکی از عوامل اصلی تقویت رشد اقتصادی آمریکا نام برده می شود از سوی دیگر اما مخالفان این طرح به ویژه دموکرات ها آن را به نفع عده ای خاص و موجب افزایش کسری بودجه دولت دانسته اند. به نظر می رسد با بازگشایی کسب و کارها و برگشت شاغلین به کارهای خود، رفته رفته حس اطمینان مصرف کنندگان حتی با وجود ادامه پیشتازی های کرونا رو به بهبود است؛ تا جایی که شاخص مدیران تولید آمریکا در ماه ژوئیه به ۵۴.۲ درصد رسید که این رقم ۱.۶ درصد بیشتر از ماه قبل و ۰.۶ درصد بیشتر از پیش بینی های قبلی بوده است. بررسی ها نشان می دهد در این ماه هم میزان سفارشات داخلی و هم میزان صادرات خارجی نسبت به ماه قبل با افزایش محسوسی مواجه بوده است. شاون اوزبورن- تحلیل گر بازار ارز اسکوشیا بانک گفت: معتقدم در میان مدت دلار در موضع ضعف باقی خواهد ماند اما سقوط نخواهد کرد. این امکان وجود دارد که اگر رقبای دلار ضعیف ظاهر شوند، وضعیت به نفع دلار تغییر کند و از نظر بنیادین نیز در ماه های پیش رو دلیلی برای یک ریزش بسیار گسترده وجود ندارد. اخبار مربوط به پیشرفت در ساخت واکسن کرونا نیز دیگر خبر مهم در صدر کانون توجه معامله گران بوده است. روسیه از رضایت بخش بودن نتایج واکسن ضدکرونای خود بر روی سطح ایمنی آزمایش شوندگان خبر داده است. پیشتر نیز دانشگاه آکسفورد اعلام کرده که واکسنش با موفقیت، سطح ایمنی افراد آزمایش شده در آفریقای جنوبی، انگلیس و برزیل را افزایش داده و حالا قرار است کار آزمایش روی یک نمونه نسبتا بزرگ در آمریکا انجام شود که در صورت موفقیت آمیز بودن آن، احتمالا از اواخر امسال شاهد تولید انبوه این محصول برای عرضه جهانی آن خواهیم بود. تاکنون بیش از ۲۰ میلیون و ۶۷۵ هزار و ۳۷۷ مورد ابتلا به کرونا گزارش شده است که در این بین ۷۴۹ هزار و ۶۱ نفر جان خود را از دست داده‌اند. در بین کشورهای مختلف، بالاترین تلفات مربوط به آمریکا با ۱۶۸ هزار و ۴۳۰ نفر، برزیل با ۱۰۳ هزار و ۴۲۱ نفر، مکزیک با ۵۳ هزار و ۹۲۹ نفر، هند با ۴۷ هزار و ۱۳۸ نفر و انگلیس با ۴۶ هزار و ۷۰۶ نفر بوده است. رونالد سیمپسون، استراتژیست مسائل ارزی در موسسه اکشن اکونومیکسگفت: به صورت کلی می توان گفت اگر خبری از یک بسته حمایتی دیگر نباشد برای دلار خوب می شود چون چاپ نشدن ۳.۵ تریلیون دلار دیگر پول به بانک مرکزی در مدیریت بازارها بیشتر کمک می کند. در کوتاه مدت اما بازارها از بسته حمایتی استقبال می کنند و از به سرانجام نرسیدن آن نگران هستند. شاخص دلار شاخص دلار که نرخ برابری آن در مقابل سبدی از ارزهای جهانی را اندازه می‌گیرد، در معاملات امروز با ۰.۲۸ درصد کاهش نسبت به روز چهارشنبه در سطح ۹۳.۳۴ واحد بسته شد. نرخ برابری هر فرانک سوئیس نیز معادل ۱.۰۹۷ دلار اعلام شد. ارزهای اروپایی وضعیت بازار کار در بسیاری از کشورهای اروپایی رو به بهود است و این مساله می تواند نویدبخش رشد مخارج مصارفی خانوارهای اروپایی در ماه های پیش رو باشد. طبق اعلام مرکز آمار انگلیس، نرخ بیکاری این کشور تا پایان ماه ژوئیه بدون تغییر نسبت به ماه قبل در سطح ۳.۹ درصدی باقی مانده است در حالی که پیش از این تصور می شد نرخ بیکاری در دومین اقتصاد بزرگ اروپا از مرز چهار درصدی بگذرد. در تازه‌ترین دور از معاملات، پوند با ۰.۳ درصد کاهش نسبت به روز قبل خود و به ازای ۱.۳۰۴ دلار مبادله شد. یورو ۰.۱۸ درصد بالا رفت و با باقی ماندن در کانال ۱.۱۷ به ۱.۱۷۹ دلار رسید. ویراج پاتل- استراتژیست ارشد ارزی در موسسه آرکرا گفت: معمولا در ماه اوت هر سال شاهد بروز الگوهای مختلف معاملاتی هستیم و امسال این روند با توجه به پیامدهای ناشی از شیوع کرونا تشدید شده است. در ماه های اخیر دارایی های پرریسک تر شاهد یک دور صعودی تند بوده اند و اگر تنش های ژئوپلتیکی باز هم تشدید شوند احتمال زیادی وجود دارد که ورق برگردد و خیلی از پیشروی های این طبقه از دارایی ها از دست برود. ارزهای آسیایی در معاملات بازارهای ارزی آسیایی، هر دلار با ۰.۳۲ درصد افزایش به ۱۰۶.۸۴۱ ین رسید. در برابر همتای استرالیایی، هر دلار آمریکا به ازای ۱.۳۹۴ دلار مبادله شد. همچنین نرخ برابری دلار معادل ۶.۹۳۳ یوان چین اعلام شد.', 'برای دومین روز متوالی، شاخص دلار ریزش کرد.', 1, 'https://www.eghtesadonline.com/%D8%A8%D8%AE%D8%B4-%D8%B7%D9%84%D8%A7-%D8%A7%D8%B1%D8%B2-7/459508-%D8%AF%D9%84%D8%A7%D8%B1-%D8%B1%DB%8C%D8%B2%D8%B4-%DA%A9%D8%B1%D8%AF/', 'https://static1.eghtesadonline.com/thumbnail/XEAN17gXJuKe/qpW0ZmpbyUytX4aLRhq9RaAF56YLGeYks4ozQKcbCVmogOVBmfOx7Qk3HgW-lbmnwSiEBbYEBcS7shnK3SoCMwwAgpg-jiJQUNVYfLfxz4g,/%D8%AF%D9%84%D8%A7%D8%B1.jpg', NULL, '۱۳۹۹/۰۵/۲۳', '۰۲:۵۷', 0, '2020-08-14 22:27:33'),
(12, 459458, 'طلا صعودی شد                      ', 'در پی اعلام رسمی رکود انگلیس        ', 'طلا-صعودی-شد', 'به گزارش اقتصاد آنلاین به نقل از فارس، با انتشار آمار ضعیف از اقتصاد انگلیس که نگرانی ها در مورد رکود اقتصادی ناشی از کرونا را تقویت کرد، قیمت طلا به 1900 دلار بازگشت و به این فلز گران بها کمک کرد تا خسارات ناشی از افزایش ارزش دلار را جبران کند. هر اونس طلا در بازارهای جهانی با 25.54 دلار افزایش 1937.43 دلار فروخته شد. طلا در ساعات اولیه معاملات آسیایی 2.5 درصد کاهش یافت. رشد اقتصادی انگلیس در سه ماهه آوریل تا ژوئن به علت شیوع کرونا، 20.4 درصد کاهش یافت که بیشترین کاهش رشد اقتصادی است که کشورها تاکنون اعلام کرده اند. جیگار تریویدی، تحلیلگر بازار گفت: «در نهایت، آنها رسما اعلام کردند برای نخستین بار از بحران مالی وارد رکود شدند. این باعث افزایش قیمت طلا و نقره شد.» علی رغم بیشترین کاهش یک روزه در بیش از 7 سال گذشته در معاملات دیروز، قیمت طلا تاکنون در سال جاری میلادی افزایش 28 درصدی را ثبت کرده است. زیرا سرمایه گذاران طلا در بحبوحه نگرانی در مورد عدم بازپرداخت ارز و ترزیق پول به بازار از سوی بانک های مرکزی، به خرید طلا روی آورده اند.', 'درحالی که طلا بیشترین کاهش یک روزه در بیش از ۷سال گذشته را در معاملات دیروز ثبت کرد اما امروز با اعلام رسمی رکود انگلیس این فلز زرد ۲۵.۵۴ دلار گران شد و به کانال ۱۹۰۰دلار بازگشت.', 1, 'https://www.eghtesadonline.com/%D8%A8%D8%AE%D8%B4-%D8%B7%D9%84%D8%A7-%D8%A7%D8%B1%D8%B2-7/459458-%D8%B7%D9%84%D8%A7-%D8%B5%D8%B9%D9%88%D8%AF%DB%8C-%D8%B4%D8%AF/', 'https://static3.eghtesadonline.com/thumbnail/pu5HUgGUigm7/qpW0ZmpbyUytX4aLRhq9RaAF56YLGeYks4ozQKcbCVmogOVBmfOx7Qk3HgW-lbmnwSiEBbYEBcS7shnK3SoCMzeZJPNcCk_sUNVYfLfxz4g,/%D8%B7%D9%84%D8%A7.jpg', NULL, '۱۳۹۹/۰۵/۲۲', '۲۱:۳۲', 0, '2020-08-14 22:27:33'),
(13, 459402, 'نگاهی به سقوط بیش از ۱۰۰دلاری طلا در یک روز/ دومین فرصت ورود به بازار طلا پیش از صعود بزرگ                      ', 'اقتصادآنلاین گزارش می‌دهد؛        ', 'نگاهی-به-سقوط-بیش-از-دلاری-طلا-در-یک-روز-دومین-فرصت-ورود-به-بازار-طلا-پیش-از-صعود-بزرگ', 'اقتصادآنلاین – اکرم شعبانی؛ در معاملات روز سه شنبه طلای آتی قابل تحویل در ماه دسامبر در بازار کومکس 1925.20 دلار در هر اونس معامله شد که بیانگر ریزش 5.61 درصدی در یک روز معاملاتی است. نقره قابل تحویل در ماه سپتامبر هم به قیمت 25.45 دلار در هر اونس معامله شد که ریزش 14.39 درصدی در بازار نقره به ثبت رسید. اگرچه بنا به گفته تحلیل‌گران این ریزش قیمتی چندان جای تعجب نداشت چرا که پیشتر بسیاری از تحلیل‌گران ریزش قیمت‌ها را تا سطح 1920 دلار در هر اونس هم پیش بینی کرده بودند، در شرایطی که سطح آتی قابل دستیابی 2000 دلار و سپس سطوح بسیار بالاتری عنوان شده است، بنا بر این تحلیل تمام این روند اصلاحی تنها در طول سه هفته اتفاق خواهد افتاد. بارت ملک رییس استراتژی جهانی TD Securities در این باره گفت: مجموعه فلزات گرانبها در طول تابستان عملکرد قابل توجهی از خود بر جای گذاشت که با کاهش نرخ بهره، انتظار مداوم افزایش تورم و افت ارزش دلار رکوردهای تاریخی در قیمت‌ها به ثبت رسید. با این وجود نوسان در بازار فلزات گرانبها از زمانی آغاز شد که ارزش دلار آمریکا به ثبات بیشتری رسید و حتی در مواردی تقویت ارزش دلار به ثبت رسید و در عین حال به صورت همزمان نرخ واقعی بهره جهت معکوس در پیش گرفت. در این باره بیشتر بخوانید: اونس طلا به چهار هزار دلار می‌رسد؟ نقش طلا در تورم‌های ناشی از بسته‌های محرک مالی صعود قدرتمند طلا برای دستیابی به سطح ۲۱۰۰دلار ملک همچنین عنوان کرد: صف آرایی قیمتی در کنار عواملی که در بالا ذکر شده از جمله تقویت ارزش دلار باعث شده بخشی از سود به دست آمده در سرمایه گذاری طلا ریزش داشته باشد؛ به دلیل خوش بینی در تصویب بسته دوم محرک مالی و نرخ واقعی بهره، ارزش دلار رو به تقویت گذاشته و این مساله موجب اشتهای ریسک پذیری در میان سرمایه گذاران شده است. بر اساس آمارهای موجود بیشترین علاقه به طلا در طی تابستان سال جاری از سوی سرمایه گذاران خرده غربی بوده و این بدان معناست که در جدول طلا سود قابل توجهی مشاهده شده است. وی افزود: تحلیل‌های کنونی بیانگر کاهش قیمت طلا و نقره است که روند نوسانات را افزایش داده و منجر به ایجاد سود در چنین اوضاع درهیم خواهد شد. سرعت خرید در صندوق‌های مبادله‌ای طلا و نقره که یکی از دلایل اصلی افزایش قیمت‌ها در طول تابستان بوده هم کاهش یافته است. با توجه به اینکه مذاکرات در مورد تصویب بسته دوم محرک مالی تریلیون دلاری همچنان ادامه دارد بسیار محتمل است که نرخ بهره و ارزش دلار تحت تاثیر این مساله قرار گرفته و شرایط بهتری تا پایان 2020 داشته باشند. ملک خاطرنشان کرد: این امکان وجود داشت که طلا حتی تا سطح 1890 دلار در اونس هم سقوط کند در همین راستا قیمت نقره قابلیت سقوط تا سطح 22 دلار در اونس داشت. بهترین خبر درباره ریزش قیمت‌ها این است که یکی از بهترین فرصت‌ها برای ورود به بازار و سرمایه گذاری ایجاد شده، ریزش قیمتی که فرصت صعود قیمت‌ها را تا پایان سال جاری و حتی بر اساس برخی نظرات تا نیمه‌های 2021 هم فراهم خواهد آورد. در همین راستا TD Securities قیمت 2100 دلار در هر اونس برای طلا و 30 دلار در هر اونس برای نقره را تا سه ماهه چهارم سال 2021 پیش بینی کرده است. با این حال پیش از دستیابی به چنین سطحی، هر گونه جهت گیری از سوی بانک فدرال رزرو می‌تواند شکل دهنده رفتار سرمایه گذاران تا پایان هفته باشد. ملک در پایان تصریح کرد: پیش از دستیابی به سطوح جدید (2100 دلار طلا و 30 دلار نقره) باید مطمئن شویم که آیا بانک فدرال رزرو قصد سرکوب دوباره نرخ بهره را در سر دارد یا خیر؟ با هدف گذاری تعدیل تورم بازارها تلاش دارند تا به این دیدگاه برسند که آیا قیمت‌ها در این سطوح ثابت خواهند ماند یا خیر؟ بر این اساس ما در TD Securities همچنان معتقدیم سطح ثبات بر روی 2100 دلار در طلا و 30 دلار در نقره خواهد بود.', 'قیمت طلا و نقره روز گذشته پس از پیش بینی‌های اصلاح بازار گام بزرگی در جهت اصلاح قیمتی برداشت. بر این اساس در طی ساعات معاملات در روز گذشته قیمت طلا بیش از ۱۰۰ دلار و نقره حدود ۴ دلار کاهش یافت.', 1, 'https://www.eghtesadonline.com/%D8%A8%D8%AE%D8%B4-%D8%B7%D9%84%D8%A7-%D8%A7%D8%B1%D8%B2-7/459402-%D9%86%DA%AF%D8%A7%D9%87%DB%8C-%D8%A8%D9%87-%D8%B3%D9%82%D9%88%D8%B7-%D8%A8%DB%8C%D8%B4-%D8%A7%D8%B2-%D8%AF%D9%84%D8%A7%D8%B1%DB%8C-%D8%B7%D9%84%D8%A7-%D8%AF%D8%B1-%DB%8C%DA%A9-%D8%B1%D9%88%D8%B2-%D8%AF%D9%88%D9%85%DB%8C%D9%86-%D9%81%D8%B1%D8%B5%D8%AA-%D9%88%D8%B1%D9%88%D8%AF-%D8%A8%D9%87-%D8%A8%D8%A7%D8%B2%D8%A7%D8%B1-%D8%B7%D9%84%D8%A7-%D9%BE%DB%8C%D8%B4-%D8%A7%D8%B2-%D8%B5%D8%B9%D9%88%D8%AF-%D8%A8%D8%B2%D8%B1%DA%AF/', 'https://static1.eghtesadonline.com/thumbnail/iT9TTsZbH3eC/qpW0ZmpbyUytX4aLRhq9RaAF56YLGeYks4ozQKcbCVmogOVBmfOx7Qk3HgW-lbmnwSiEBbYEBcS7shnK3SoCMzixH3v4g1Y-UNVYfLfxz4g,/%D8%B1%DB%8C%D8%B2%D8%B4+%D8%B5%D8%AF+%D8%AF%D9%84%D8%A7%D8%B1%DB%8C+%D8%B7%D9%84%D8%A7+%D8%AF%D8%B1+%DB%8C%DA%A9+%D8%B1%D9%88%D8%B2.jpg', NULL, '۱۳۹۹/۰۵/۲۲', '۱۶:۱۰', 0, '2020-08-14 22:27:33'),
(14, 459332, 'برگشت قیمت سکه به کانال ۱۰میلیون (۱۳۹۹/۵/۲۲)                      ', NULL, 'برگشت-قیمت-سکه-به-کانال-میلیون', 'به گزارش اقتصادآنلاین به نقل از تسنیم، هم اکنون در بازار داخلی قیمت طلای 18عیار هر گرم یک میلیون و 4 هزار تومان، قیمت دلار 22 هزار تومان و قیمت سکه تمام‌بهار آزادی طرح جدید 10 میلیون و 300 تومان و قیمت سکه طرح قدیم 10 میلیون تومان است. همچنین در بازار سبزه‌میدان، قیمت نیم‌سکه بهار آزادی 5میلیون و 200 هزار تومان، ربع‌سکه بهار آزادی 2میلیون و 950 هزار تومان و سکه گرمی یک‌میلیون و 700 هزار تومان تعیین شده است و به فروش می‌رسد. قیمت هر گرم طلای 18عیار هم یک میلیون و 4 هزار تومان، هرگرم طلای 24عیار یک‌میلیون و 375 هزار تومان و هر مثقال طلا 4میلیون و 350 هزار تومان ارزش‌گذاری شده است، ضمن آنکه هر اونس طلا در بازارهای جهانی با قیمت 1897 دلار معامله می‌شود. تغییر روند افزایش قیمت طلا و نقره آغاز شده است. طلا پس از تلاش ناموفقش برای رسیدن به بالاترین رقم در روز دوشنبه، روند کاهشی خود را آغاز کرده است.', 'در بازار آزاد قیمت سکه تمام‌بهار آزادی طرح جدید ۱۰ میلیون و ۳۰۰ هزار تومان است.', 1, 'https://www.eghtesadonline.com/%D8%A8%D8%AE%D8%B4-%D8%B7%D9%84%D8%A7-%D8%A7%D8%B1%D8%B2-7/459332-%D8%A8%D8%B1%DA%AF%D8%B4%D8%AA-%D9%82%DB%8C%D9%85%D8%AA-%D8%B3%DA%A9%D9%87-%D8%A8%D9%87-%DA%A9%D8%A7%D9%86%D8%A7%D9%84-%D9%85%DB%8C%D9%84%DB%8C%D9%88%D9%86/', 'https://static3.eghtesadonline.com/thumbnail/O7GV9Clm6TUB/qpW0ZmpbyUytX4aLRhq9RaAF56YLGeYks4ozQKcbCVmogOVBmfOx7Qk3HgW-lbmnwSiEBbYEBcS7shnK3SoCM6wd-WtNwmW-UNVYfLfxz4g,/%DA%AF%D8%A7%D9%84%D8%B1%DB%8C+%D8%B3%DA%A9%D9%87.jpg', NULL, '۱۳۹۹/۰۵/۲۲', '۱۲:۱۰', 0, '2020-08-14 22:27:33'),
(15, 459299, 'آخرین قیمت دلار (۱۳۹۹/۵/۲۲)                      ', NULL, 'آخرین-قیمت-دلار', 'به گزارش اقتصادآنلاین به نقل از تسنیم، صرافی‌های بانک‌ها امروز چهارشنبه قیمت دلار را برای فروش به مردم با کاهش 80 تومانی نسبت به قیمت‌های پایانی روز کاری قبل 21 هزار و 800 تومان اعلام کرده‌اند. این صرافی‌ها دلار را از مردم با قیمت 21 هزار و 700 تومان خریداری می‌کنند. طی روز جاری قیمت فروش یورو در صرافی‌های بانک‌ها نیز با کاهش 30 تومانی نسبت به قیمت‌های پایانی روز کاری قبل 25 هزار و 600 تومان اعلام شده است؛ قیمت خرید یورو در این صرافی‌ها 25 هزار و 550 تومان درج شده است. بسیاری از بازاری‌ها پیش‌بینی می‌کنند که به‌زودی ریزش شدید قیمت‌ها در بازار ارز رخ خواهد داد و این موضوع حتی در کانال‌های دلالی ارز نیز مطرح شده است و به خریداران هشدار می‌دهند که فعلاً در قیمت‌های بالا خرید ارز نداشته باشند چراکه حتماً ریزشی سنگین در راه است.', 'صرافی‌های بانکی امروز، قیمت خرید و فروش دلار را نسبت به روز کاری قبل ۸۰ تومان کاهش دادند و برهمین اساس هم اکنون قیمت دلار در صرافی‌های بانکی برای فروش ۲۱ هزار و ۸۰۰ تومان است.', 1, 'https://www.eghtesadonline.com/%D8%A8%D8%AE%D8%B4-%D8%B7%D9%84%D8%A7-%D8%A7%D8%B1%D8%B2-7/459299-%D8%A2%D8%AE%D8%B1%DB%8C%D9%86-%D9%82%DB%8C%D9%85%D8%AA-%D8%AF%D9%84%D8%A7%D8%B1/', 'https://static3.eghtesadonline.com/thumbnail/nu1XfoDy6qwf/qpW0ZmpbyUytX4aLRhq9RaAF56YLGeYks4ozQKcbCVmogOVBmfOx7Qk3HgW-lbmnwSiEBbYEBcS7shnK3SoCM-614d2esc-VUNVYfLfxz4g,/%DA%AF%D8%A7%D9%84%D8%B1%DB%8C+%D8%AF%D9%84%D8%A7%D8%B1.jpg', NULL, '۱۳۹۹/۰۵/۲۲', '۱۱:۰۷', 0, '2020-08-14 22:27:33'),
(16, 459288, 'نرخ رسمی یورو و پوند بانکی کم شد                      ', 'براساس اعلام بانک مرکزی،        ', 'نرخ-رسمی-یورو-پوند-بانکی-کم-شد', 'به گزارش اقتصادآنلاین به نقل از ایبِنا، براساس اعلام بانک مرکزی هر دلار آمریکا برای امروز (چهارشنبه بیست و دوم مرداد ماه ۹۹) بدون تغییر نسبت به روز گذشته، ۴۲ هزار ریال قیمت خورد. همچنین هر پوند انگلیس با کاهش ۲۵۲ ریالی به قیمت ۵۴ هزار و ۷۱۱ ریال و هر یورو نیز با کاهش ۱۱۶ ریالی به قیمت ۴۹ هزار و ۲۱۲ ریال اعلام شد. افزون بر این، هر فرانک سوئیس۴۵ هزار و ۶۸۹ ریال، کرون سوئد ۴ هزار و ۷۸۱ ریال، کرون نروژ ۴ هزار و ۶۴۶ ریال، کرون دانمارک ۶ هزار و ۶۰۸ ریال، روپیه هند ۵۶۳ ریال، درهم امارات متحده عربی ۱۱ هزار و ۴۳۷ ریال، دینار کویت ۱۳۷ هزار و ۳۰۳ ریال، یکصد روپیه پاکستان ۲۴ هزار و ۹۴۵ ریال، یکصد ین ژاپن ۳۹ هزار و ۳۴۸ ریال، دلار هنگ‌ کنگ ۵ هزار و ۴۲۰ ریال، ریال عمان ۱۰۹ هزار و ۲۳۴ ریال و دلار کانادا ۳۱ هزار و ۴۷۹ ریال قیمت خورد. از سوی دیگر، نرخ دلار نیوزیلند ۲۷ هزار و ۵۱۳ ریال، راند آفریقای جنوبی دو هزار و ۳۹۲ ریال، لیر ترکیه ۵ هزار و ۸۰۰ ریال، روبل روسیه ۵۷۲ ریال، ریال قطر ۱۱ هزار و ۵۳۹ ریال، یکصد دینار عراق ۳ هزار و ۵۱۸ ریال، لیر سوریه ۸۳ ریال، دلار استرالیا ۲۹ هزار و ۸۸۸ ریال، ریال سعودی ۱۱ هزار و ۲۰۰ ریال، دینار بحرین ۱۱۱ هزار و ۷۰۴ ریال، دلار سنگاپور ۳۰ هزار و ۵۴۷ ریال، یکصد تاکای بنگلادش ۴۹ هزار و ۴۹۶ ریال، ده روپیه سریلانکا ۲ هزار و ۲۷۱ ریال، کیات میانمار ۳۱ ریال و یکصد روپیه نپال ۳۴ هزار و ۹۸۶ ریال تعیین شد. همچنین، نرخ یکصد درام ارمنستان ۸ هزار و ۷۲۱ ریال، دینار لیبی ۳۰ هزار و ۶۶۱ ریال، یوان چین ۶ هزار و ۳۸ ریال، یکصد بات تایلند ۱۳۴ هزار ۸۵۰ ریال، رینگیت مالزی ۱۰ هزار و ۲ ریال، یک هزار وون کره جنوبی ۳۵ هزار و ۴۰۲ ریال، دینار اردن ۵۹ هزار و ۲۳۹ یال، یکصد تنگه قزاقستان ۱۰ هزار و ۳۴ ریال، لاری گرجستان ۱۳ هزار و ۶۶۸ ریال، یک هزار روپیه اندونزی ۲ هزار و ۸۴۵ ریال، افغانی افغانستان ۵۴۷ ریال، روبل جدید بلاروس ۱۷ هزار ۸ ریال، منات آذربایجان ۲۴ هزار و ۷۱۸ ریال، یکصد پزوی فیلیپین ۸۵ هزار و ۷۶۳ ریال، سومونی تاجیکستان ۴ هزار و ۶۶ ریال، بولیوار جدید ونزوئلا ۴ هزار و ۲۰۶ ریال و منات جدید ترکمنستان ۱۱ هزار و ۹۶۷ ریال ارزش‌گذاری شد.', 'بانک مرکزی نرخ رسمی ۴۷ ارز را برای امروز اعلام کرد که بر اساس آن نرخ ۱۶ ارز افزایش و ۲۲ واحد پولی کاهش یافت. نرخ دلار و ۸ ارز دیگر نیز ثابت بود.', 1, 'https://www.eghtesadonline.com/%D8%A8%D8%AE%D8%B4-%D8%B7%D9%84%D8%A7-%D8%A7%D8%B1%D8%B2-7/459288-%D9%86%D8%B1%D8%AE-%D8%B1%D8%B3%D9%85%DB%8C-%DB%8C%D9%88%D8%B1%D9%88-%D9%BE%D9%88%D9%86%D8%AF-%D8%A8%D8%A7%D9%86%DA%A9%DB%8C-%DA%A9%D9%85-%D8%B4%D8%AF/', 'https://static1.eghtesadonline.com/thumbnail/owaU3RpegF30/qpW0ZmpbyUytX4aLRhq9RaAF56YLGeYks4ozQKcbCVmogOVBmfOx7Qk3HgW-lbmnwSiEBbYEBcS7shnK3SoCM_7NeYSIOnxEUNVYfLfxz4g,/%DA%AF%D8%A7%D9%84%D8%B1%DB%8C+%D8%AF%D9%84%D8%A7%D8%B1.jpg', NULL, '۱۳۹۹/۰۵/۲۲', '۱۰:۳۰', 0, '2020-08-14 22:27:33'),
(17, 459277, 'قیمت جهانی طلا سقوط کرد                      ', 'در شروع معاملات امروز؛        ', 'قیمت-جهانی-طلا-سقوط-کرد', 'به گزارش اقتصادآنلاین به نقل از مهر، صبح امروز چهارشنبه، قیمت طلا در حالی بیش از ۲ درصد سقوط کرد که روز گذشته با سقوطی ۵.۳ درصدی بدترین روز خود در ۷ سال اخیر را ثبت کرده بود. امید به بسته حمایتی جدید آمریکا و داده‌های اقتصادی قدرتمند باعث شد تا اس‌اندپی ۵۰۰ به رکورد تاریخی خود نزدیک شود. قیمت نقره هم دیشب بیش از ۱۳.۸ درصد سقوط کرد تا بدترین سقوط یک روزه خود از اکتبر ۲۰۰۸ تا کنون را ثبت کند. قیمت پلاتینیوم و پالادیوم هم ۴.۷ درصد سقوط کرد. بازگشت قیمت‌های تولیدکنندگان آمریکا در ماه جولای فراتر از انتظار بود. تا ساعت ۷:۰۸ به وقت تهران، قیمت خرید نقدی هر اونس طلا در بورس لندن ۰.۸۲ درصد یا ۱۵ دلار پایین آمد و به ۱۸۹۵.۵۷ دلار رسید. این شاخص دیشب ۵.۲ درصد سقوط کرده بود. قیمت پیش‌خرید هر اونس طلای آمریکا صبح امروز ۲.۳۱ درصد یا ۴۵ دلار سقوط کرد و به ۱۹۰۱.۳۰ دلار رسید. این شاخص در معاملات دیشب ۴.۷ درصد سقوط کرده بود.', 'قیمت طلا در شروع معاملات امروز چهارشنبه، در حالی بیش از ۲ درصد سقوط کرد که دیروز با سقوطی ۵.۳ درصدی بدترین روز خود در ۷ سال اخیر را ثبت کرده بود.', 1, 'https://www.eghtesadonline.com/%D8%A8%D8%AE%D8%B4-%D8%B7%D9%84%D8%A7-%D8%A7%D8%B1%D8%B2-7/459277-%D9%82%DB%8C%D9%85%D8%AA-%D8%AC%D9%87%D8%A7%D9%86%DB%8C-%D8%B7%D9%84%D8%A7-%D8%B3%D9%82%D9%88%D8%B7-%DA%A9%D8%B1%D8%AF/', 'https://static3.eghtesadonline.com/thumbnail/730wo2XPOnWW/qpW0ZmpbyUytX4aLRhq9RaAF56YLGeYks4ozQKcbCVmogOVBmfOx7Qk3HgW-lbmnwSiEBbYEBcS7shnK3SoCMz9usZ10oUoOUNVYfLfxz4g,/iiNZ6z4ex1KN.jpg', NULL, '۱۳۹۹/۰۵/۲۲', '۰۹:۵۷', 0, '2020-08-14 22:27:33');
